import jsPDF from "jspdf";
import autoTable from "jspdf-autotable";

export const exportToPDF = () => {
  const doc = new jsPDF({ orientation: "landscape", format: "a4" });

  const head = [["Naam", "ma 1", "ma 2", "di 1"]];
  const body = [
    ["Testpersoon", "schep", "bereider", "âœ“"]
  ];

  autoTable(doc, {
    head,
    body,
    startY: 20,
    styles: {
      fontSize: 10,
      cellPadding: 4,
      halign: "center",
      valign: "middle",
    },
    headStyles: {
      fillColor: [41, 128, 185],
      textColor: [255, 255, 255],
    },
    didDrawCell: function (data) {
      const val = (Array.isArray(data.cell.text) ? data.cell.text.join(" ") : data.cell.text || "").trim().toLowerCase();
      console.log("ðŸŸ¡ Celwaarde:", val);

      const kleuren = {
        "bereider": [30, 58, 138],
        "schep": [17, 24, 39],
        "âœ“": [144, 238, 144],
      };

      if (kleuren[val]) {
        data.cell.styles.fillColor = kleuren[val];
        data.cell.styles.textColor = [255, 255, 255];
      }
    }
  });

  doc.save("kleurtest.pdf");
};
