import React, { useState, useEffect } from "react";
import jsPDF from "jspdf";
import autoTable from "jspdf-autotable";
import {
  getShiftCountPerMedewerker,
  importeerBeschikbaarheidKnop,
} from "./utils/plannerHelpers";
import { dagMap } from "./utils/dagen";

const dagen = ["ma", "di", "wo", "do", "vr", "za", "zo"];
const shifts = [1, 2];

export default function PlannerBoard({ beschikbaarheid: beschikbaarheidProp, planning, setPlanning, onTotalLoonkostenChange }) {
  const [medewerkers, setMedewerkers] = useState(() => {
    try {
      const saved = localStorage.getItem("medewerkers");
      const parsed = saved ? JSON.parse(saved) : [];
      return Array.isArray(parsed) ? parsed.filter((m) => m && typeof m === "object" && m.naam) : [];
    } catch {
      return [];
    }
  });

  const [beschikbaarheid, setLocalBeschikbaarheid] = useState(() => {
    try {
      const saved = localStorage.getItem("beschikbaarheid");
      return saved ? JSON.parse(saved) : {};
    } catch {
      return {};
    }
  });

  useEffect(() => {
    if (beschikbaarheidProp && Object.keys(beschikbaarheidProp).length > 0) {
      setLocalBeschikbaarheid(beschikbaarheidProp);
      localStorage.setItem("beschikbaarheid", JSON.stringify(beschikbaarheidProp));
    }
  }, [beschikbaarheidProp]);

  const [popup, setPopup] = useState(null);
  const shiftCountPerMedewerker = getShiftCountPerMedewerker(planning);

  const gestructureerdeUpdate = (prev, popup, functie, soort) => {
    return {
      ...prev,
      [popup.medewerker]: {
        ...prev[popup.medewerker],
        [popup.dag]: {
          ...prev[popup.medewerker]?.[popup.dag],
          [popup.shift]: { functie, soort },
        },
      },
    };
  };

  const updatePlanning = (functie, soort) => {
    if (popup) {
      setPlanning((prev) => {
        const nieuwePlanning = gestructureerdeUpdate(prev, popup, functie, soort);
        localStorage.setItem("planning", JSON.stringify(nieuwePlanning));
        return nieuwePlanning;
      });
      setPopup(null);
    }
  };

  useEffect(() => {
    const loonkostenPerUur = { 16: 6.5, 17: 7, 18: 8, 19: 9, 20: 10, 21: 11, 22: 12, 23: 13, 24: 14, 25: 15 };
    let totaal = 0;

    medewerkers.forEach((m) => {
      dagen.forEach((dag) => {
        shifts.forEach((shift) => {
          const entry = planning[m.naam]?.[dag]?.[shift];
          if (entry) {
            const uren = entry.soort === "standby" ? 4 : 6;
            const leeftijd = typeof m.leeftijd === 'number' ? m.leeftijd : 18;
            const uurloon = loonkostenPerUur[leeftijd] ?? 15;
            totaal += uren * uurloon;
          }
        });
      });
    });

    if (typeof onTotalLoonkostenChange === 'function') {
      onTotalLoonkostenChange(totaal);
    }
  }, [planning, medewerkers]);

  const exportToPDF = () => {
    const doc = new jsPDF({ orientation: "landscape", format: "a4" });

    const tableColumn = ["Naam", ...dagen.flatMap(dag => shifts.map(shift => `${dag} ${shift}`))];
    const body = medewerkers.map((m) => {
      const count = shiftCountPerMedewerker[m.naam] || 0;
      const max = m.maxShifts ?? "?";
      const leeftijd = m.leeftijd ?? "?";
      const row = { Naam: `${m.naam.split(" ").map((part, i) => i === 0 ? part : part.charAt(0).toUpperCase() + part.slice(1)).join(" ")} [${leeftijd}] (${count}/${max})` };

      dagen.forEach(dag => {
        shifts.forEach(shift => {
          const key = `${dag} ${shift}`;
          const entry = planning[m.naam]?.[dag]?.[shift];
          const naamKey = m.naam?.toLowerCase();
          const beschikbaar = beschikbaarheid?.[naamKey]?.[dagMap[dag]]?.[shift];

          if (entry) {
            if (entry.functie === "schepper") row[key] = entry.soort === "vast" ? "schep" : "schep(s)";
            else if (entry.functie === "ijsbereider") row[key] = entry.soort === "vast" ? "bereider" : "bereider(s)";
            else if (entry.functie === "ijsvoorbereider") row[key] = entry.soort === "vast" ? "prep" : "prep(s)";
            else row[key] = `${entry.functie} (${entry.soort})`;
          } else if (beschikbaar) {
            row[key] = "✓";
          } else {
            row[key] = "";
          }
        });
      });

      return row;
    });

    autoTable(doc, {
      columnStyles: { Naam: { halign: 'left' } },
      didParseCell: function (data) {
        const val = (typeof data.cell.raw === "string" ? data.cell.raw : Array.isArray(data.cell.raw) ? data.cell.raw.join(" ") : "").toLowerCase().trim();
        const colKey = data.column.dataKey;

        if (val === "bereider") {
          data.cell.styles.fillColor = [30, 58, 138];
          data.cell.styles.textColor = [255, 255, 255];
        } else if (val === "bereider(s)") {
          data.cell.styles.fillColor = [96, 165, 250];
        } else if (val === "prep") {
          data.cell.styles.fillColor = [191, 219, 254];
        } else if (val === "prep(s)") {
          data.cell.styles.fillColor = [219, 234, 254];
        } else if (val === "schep") {
          data.cell.styles.fillColor = [250, 204, 21];
        } else if (val === "schep(s)") {
          data.cell.styles.fillColor = [254, 240, 138];
        } else if (val === "✓") {
          data.cell.styles.fillColor = [144, 238, 144];
        } else if (val === "" && colKey !== "Naam") {
          const naam = data.row.raw["Naam"].toLowerCase();
          const [dagLabel, shiftStr] = colKey.split(" ");
          const shift = parseInt(shiftStr);
          const dagMap = {
            ma: "maandag", di: "dinsdag", wo: "woensdag",
            do: "donderdag", vr: "vrijdag", za: "zaterdag", zo: "zondag"
          };
          const dag = Object.entries(dagMap).find(([k]) => dagLabel.toLowerCase().startsWith(k))?.[1];
          if (naam && dag && beschikbaarheid?.[naam]?.[dag]?.[shift]) {
            data.cell.styles.fillColor = [144, 238, 144]; // lichtgroen
          }
        }
      },
      columns: tableColumn.map(key => ({ header: key, dataKey: key, halign: key === 'Naam' ? 'left' : 'center' })),
      body,
      startY: 20,
      styles: {
        fontSize: 8,
        halign: "center",
        valign: "middle",
        cellPadding: 2,
        lineWidth: 0.1,
        lineColor: [0, 0, 0],},
      headStyles: {
        fillColor: [41, 128, 185],
        textColor: [255, 255, 255],
        fontStyle: "bold",
      },
      didDrawCell: function (data) {
        const val = (typeof data.cell.text === "string" ? data.cell.text : Array.isArray(data.cell.text) ? data.cell.text.join(" ") : "").toLowerCase().trim();

        if (val === "bereider") {
          data.cell.styles.fillColor = [30, 58, 138];
          data.cell.styles.textColor = [255, 255, 255];
        } else if (val === "bereider(s)") {
          data.cell.styles.fillColor = [96, 165, 250];
        } else if (val === "prep") {
          data.cell.styles.fillColor = [191, 219, 254];
        } else if (val === "prep(s)") {
          data.cell.styles.fillColor = [219, 234, 254];
        } else if (val === "schep") {
          data.cell.styles.fillColor = [250, 204, 21];
        } else if (val === "schep(s)") {
          data.cell.styles.fillColor = [254, 240, 138];
        }
      }
    });

    const eindY = doc.lastAutoTable.finalY + 20;
    doc.setFontSize(8);


    const loonkostenPerUur = { 16: 6.5, 17: 7, 18: 8, 19: 9, 20: 10, 21: 11, 22: 12, 23: 13, 24: 14, 25: 15 };
    const loonkostenPerDag = {};
    const shiftTelling = {};


    medewerkers.forEach((m) => {
      const uurloon = parseFloat((loonkostenPerUur[m.leeftijd] ?? 15).toFixed(2));
      dagen.forEach((dag) => {
        let dagTotaal = loonkostenPerDag[dag] || 0;
        shiftTelling[dag] = shiftTelling[dag] || { schepper: 0, ijsbereider: 0, ijsvoorbereider: 0 };

        shifts.forEach((shift) => {
          const entry = planning[m.naam]?.[dag]?.[shift];
          if (entry) {
            const uren = entry.soort === "standby" ? 4 : 6;
            dagTotaal += uren * uurloon;
            if (shiftTelling[dag][entry.functie] !== undefined) {
              shiftTelling[dag][entry.functie]++;
            }
          }
        });

        loonkostenPerDag[dag] = dagTotaal;
      });
    });

    const startY = doc.lastAutoTable.finalY + 35;
    autoTable(doc, {
      head: [["Dag"].concat(dagen.flatMap(d => shifts.map(s => `${d} shift ${s}`)))],
      body: [
        ["Loonkosten"].concat(dagen.flatMap(d => shifts.map(s => `€ ${(() => {
          let totaal = 0;
          medewerkers.forEach((m) => {
            const entry = planning[m.naam]?.[d]?.[s];
            if (entry) {
              const uren = entry.soort === "standby" ? 4 : 6;
              const loonkostenPerUur = { 16: 6.5, 17: 7, 18: 8, 19: 9, 20: 10, 21: 11, 22: 12, 23: 13, 24: 14, 25: 15 };
              const leeftijd = typeof m.leeftijd === 'number' ? m.leeftijd : 18;
              const uurloon = loonkostenPerUur[leeftijd] ?? 15;
              totaal += uren * uurloon;
            }
          });
          return totaal.toFixed(2);
        })()}`))),
        ["Bereiders"].concat(dagen.flatMap(d => shifts.map(s => medewerkers.filter(m => planning[m.naam]?.[d]?.[s]?.functie === "ijsbereider").length))),
        ["Voorbereiders"].concat(dagen.flatMap(d => shifts.map(s => medewerkers.filter(m => planning[m.naam]?.[d]?.[s]?.functie === "ijsvoorbereider").length))),
        ["Scheppers"].concat(dagen.flatMap(d => shifts.map(s => medewerkers.filter(m => planning[m.naam]?.[d]?.[s]?.functie === "schepper").length)))
      ],
      startY,
      styles: {
        fontSize: 8,
        halign: 'center',
        valign: 'middle',
        cellPadding: 2,
        lineWidth: 0.1,
        lineColor: [0, 0, 0]
      },
      headStyles: { fillColor: [100, 149, 237], textColor: 255 },
      columnStyles: { 0: { fontStyle: 'bold', halign: 'left' } }
    });

    const totaalLoonkosten = medewerkers.reduce((totaal, m) => {
  const uurloon = loonkostenPerUur[m.leeftijd] ?? 15;
  return totaal + dagen.reduce((dagTotaal, dag) => {
    return dagTotaal + shifts.reduce((shiftTotaal, shift) => {
      const entry = planning[m.naam]?.[dag]?.[shift];
      if (!entry) return shiftTotaal;
      const uren = entry.soort === 'standby' ? 4 : 6;
      return shiftTotaal + uren * uurloon;
    }, 0);
  }, 0);
}, 0);

// Weergeven totaal loonkosten onderaan PDF
const laatsteY = doc.lastAutoTable?.finalY ?? 150;
if (laatsteY + 20 > doc.internal.pageSize.getHeight()) {
  doc.addPage();
}

doc.setFontSize(12);
doc.setTextColor(40);
doc.setFont(undefined, 'bold');
doc.text(`Totale loonkosten deze week: €${totaalLoonkosten.toFixed(2)}`, 14, doc.lastAutoTable?.finalY + 20 || 20);



doc.save("planning_export.pdf");
  };

  return (
    <div className="p-4 bg-gray-100">
      <div className="text-right text-lg font-bold mb-2">
        Totale loonkosten worden hier getoond via App.jsx
      </div>


      <div className="overflow-x-auto mb-4">
  <table className="text-xs border-collapse w-full bg-white shadow">
  <thead>
    <tr>
      <th className="border px-2 py-1">Type</th>
      {dagen.map((dag) => (
        shifts.map((shift) => (
          <th key={`${dag}-${shift}`} className="border px-2 py-1 text-center">
            {dagMap[dag]} shift {shift}
          </th>
        ))
      ))}
    </tr>
  </thead>
  <tbody>
    {['Bereiders', 'Voorbereiders', 'Scheppers', 'Kosten'].map((type) => (
      <tr key={type}>
        <td className="border px-2 py-1 font-semibold">{type}</td>
        {dagen.map((dag) => (
          shifts.map((shift) => {
            let value = 0;
            if (type === 'Kosten') {
              value = medewerkers.reduce((totaal, m) => {
                const entry = planning[m.naam]?.[dag]?.[shift];
                if (!entry) return totaal;
                const uren = entry.soort === 'standby' ? 4 : 6;
                const loonkostenPerUur = { 16: 6.5, 17: 7, 18: 8, 19: 9, 20: 10, 21: 11, 22: 12, 23: 13, 24: 14, 25: 15 };
                const leeftijd = typeof m.leeftijd === 'number' ? m.leeftijd : 18;
                const uurloon = loonkostenPerUur[leeftijd] ?? 15;
                return totaal + uren * uurloon;
              }, 0);
            } else {
              const functieMap = {
                'Bereiders': 'ijsbereider',
                'Voorbereiders': 'ijsvoorbereider',
                'Scheppers': 'schepper'
              };
              const functie = functieMap[type];
              value = medewerkers.filter(m => planning[m.naam]?.[dag]?.[shift]?.functie === functie).length;
            }
            return (
              <td key={`${type}-${dag}-${shift}`} className="border px-2 py-1 text-center">
                {type === 'Kosten' ? `€ ${Math.round(value)}` : value}
              </td>
            );
          })
        ))}
      </tr>
    ))}
  </tbody>
</table>
</div>
      <div className="flex gap-2 items-center mb-4">
      {importeerBeschikbaarheidKnop(setLocalBeschikbaarheid, setMedewerkers)}
        <button onClick={exportToPDF} className="bg-red-600 text-white px-4 py-2 rounded shadow">
          Exporteer naar PDF
        </button>
      </div>

      {medewerkers.length === 0 && (
        <p className="text-gray-600">Nog geen medewerkers geladen. Importeer beschikbaarheid om te starten.</p>
      )}

      {medewerkers.length > 0 && (
        <table className="table-fixed border w-full bg-white text-xs font-sans">
          <thead>
            <tr>
            <th className="border px-4 py-2 text-left w-60">Naam</th>
              {dagen.map((dag) =>
                shifts.map((shift) => (
                  <th key={`${dag}-${shift}`} className="border px-2 py-1 text-center">
                    {dag} {shift}
                  </th>
                ))
              )}
            </tr>
          </thead>
          <tbody>
          {medewerkers.map((m) => (
  <tr key={m.naam}>
{(() => {
  const count = shiftCountPerMedewerker[m.naam] || 0;
  const max = m.maxShifts ?? "?";
  const leeftijd = m.leeftijd ?? "?";
  const bgClass =
    count > max ? "bg-red-200" :
    count < max ? "bg-yellow-200" :
    "";

  return (
    <td
      className={`border px-4 py-2 text-left whitespace-nowrap w-60 font-bold ${bgClass}`}
      title={m.opmerking || ""}>
      {m.naam.split(" ").map((part, i) => i === 0 ? part : part.charAt(0).toUpperCase() + part.slice(1)).join(" ")} [{leeftijd}] ({count}/{max})
    </td>

  );
})()}

    {dagen.map((dag) =>
      shifts.map((shift) => {
        const naamKey = m.naam?.toLowerCase();
        const entry = planning[m.naam]?.[dag]?.[shift];
        const beschikbaar = beschikbaarheid?.[naamKey]?.[dagMap[dag]]?.[shift];

        let text = "";
        let bgColor = "#ffffff"; // standaard wit

        if (entry) {
          switch (entry.functie) {
            case "schepper":
              text = entry.soort === "vast" ? "schep" : "schep(s)";
              bgColor = entry.soort === "vast" ? "#facc15" : "#fef08a";
              break;
            case "ijsbereider":
              text = entry.soort === "vast" ? "bereider" : "bereider(s)";
              bgColor = entry.soort === "vast" ? "#1e3a8a" : "#60a5fa";
              break;
            case "ijsvoorbereider":
              text = entry.soort === "vast" ? "prep" : "prep(s)";
              bgColor = entry.soort === "vast" ? "#bfdbfe" : "#dbeafe";
              break;
            default:
              text = `${entry.functie} (${entry.soort})`;
              break;
          }
        } else if (beschikbaar) {
          // Geen planning maar wél beschikbaarheid → lichtgroen
          bgColor = "#bbf7d0";
          text = "✓";
        }

        return (
          <td
            key={`${m.naam}-${dag}-${shift}`}
            title={`Shift ${shift} op ${dagMap[dag]}`}
  className="border text-center cursor-pointer"
  onClick={() => {
    const entry = planning[m.naam]?.[dag]?.[shift];
    if (entry) {
      setPlanning((prev) => {
        const nieuw = { ...prev };
        delete nieuw[m.naam][dag][shift];
        if (Object.keys(nieuw[m.naam][dag]).length === 0) delete nieuw[m.naam][dag];
        if (Object.keys(nieuw[m.naam]).length === 0) delete nieuw[m.naam];
        localStorage.setItem("planning", JSON.stringify(nieuw));
        return nieuw;
      });
    } else {
      setPopup({ medewerker: m.naam, dag, shift });
    }
  }}
  style={{ backgroundColor: bgColor, color: entry?.functie === "ijsbereider" && entry?.soort === "vast" ? "white" : undefined }}
>
  {text}
</td>

        );
      })
    )}
  </tr>


))}



          </tbody>
        </table>
      )}

      {popup && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white p-4 rounded shadow-md w-[300px]">
            <h2 className="font-bold mb-4 text-center">
              {popup.medewerker} - {popup.dag} Shift {popup.shift}
            </h2>
            <div className="grid grid-cols-2 gap-2">
              {["ijsbereider", "ijsvoorbereider", "schepper"].map((functie) =>
                ["vast", "standby"].map((soort) => {
                  let kleur = "";
                  let label = "";

                  if (functie === "ijsbereider") {
                    kleur = soort === "vast" ? "bg-blue-900 text-white" : "bg-blue-300 text-black";
                    label = soort === "vast" ? "bereider" : "⏱️ bereider";
                  } else if (functie === "ijsvoorbereider") {
                    kleur = soort === "vast" ? "bg-blue-600 text-white" : "bg-blue-200 text-black";
                    label = soort === "vast" ? "prep" : "⏱️ prep";
                  } else if (functie === "schepper") {
                    kleur = soort === "vast" ? "bg-yellow-500 text-black" : "bg-yellow-200 text-black";
                    label = soort === "vast" ? "schep" : "⏱️ schep";
                  }

                  return (
                    <button
                      key={`${functie}-${soort}`}
                      className={`${kleur} px-3 py-2 rounded font-medium text-sm`}
                      onClick={() => updatePlanning(functie, soort)}
                    >
                      {label}
                    </button>
                  );
                })
              )}
            </div>
            <div className="mt-4 text-center">
              <button onClick={() => setPopup(null)} className="text-gray-600 text-sm underline">
                Annuleren
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
